import styled from 'styled-components'
import { useState, useEffect } from 'react'
import NavItem from './NavItem'


const StyledAdminMenu = styled.div`
  background: ${props => props.theme.white};
  width: 300px;
  /* display: none; */
  position: absolute;
  vertical-align: baseline;
  top: 78em;
  a {
    color: ${props => props.theme.black};
  }
  :hover {

    .admin-submenu {
      display: block;
    }
  }
  z-index: 2;
`

const AdminMenu = () => {
  return (
    <StyledAdminMenu>
      <NavItem route="/manage-books" pageName="Manage Books" className="admin-submenu"/>
      <NavItem route="/manage-events" pageName="Manage Events" className="admin-submenu" />
      <NavItem route="/checkouts" pageName="Manage Checkouts" className="admin-submenu" />
    </StyledAdminMenu>
  )
}

export default AdminMenu
