import { useState } from 'react'


const NoteApp = () => {
  const [notes, setNotes] = useState([])
  const [title, setTitle] = useState('')

  const addNote = (e) => {
    e.preventDefault()
    setNotes([
      ...notes,
      {title}
    ])
    setTitle('')
  }

  const handleDeleteNote = (title) => {
    setNotes(notes.filter(() => note.title !== title))
  }

  return (
    <div>
      <h1>Notes</h1>

      {notes.map((note) => (
        <div key={note.title}>
          <h3>{note.title}</h3>
          <button onClick={handleDeleteNote}>x</button>
        </div>
      ))}

      <p>Add note</p>
      <form onSubmit={addNote}>
        <input 
          value={title} 
          onChange={(e) => {setTitle(e.target.value)}} 
        />
        <button>add note</button>

      </form>

    </div>
  )
}

export default NoteApp
