import styled from 'styled-components'



const StyledFeature = styled.div`
  display: flex;
  width: 100%;

`

const StyledFeatureBody = styled.div`
  /* background-color: ${props => props.theme.black}; */
  background-color: ${props => props.color};
  flex: ${props => props.size};
  color: ${props => props.theme.white};
  box-shadow: 0px 10px 15px -3px rgba(0,0,0,0.1); /*tailwindcss large shadow*/
`

const StyledFeatureImage = styled.div`
  background-color: ${props => props.theme.red};;
  flex: ${props => props.size};
`

export const LeftFeature = () => (
  <StyledFeature>
    <StyledFeatureBody size="2" color={props => props.theme.white}>
      <h2>Internet entertainment. Global original content. Product personalization.</h2>
      <p>Our first original series debuted in 2013. Over the following decades, Internet TV will replace linear, and we hope to keep loading by offering an amazing entertainment experience.</p>
      <p></p>
    </StyledFeatureBody>
    <StyledFeatureImage size="1">
      <p>hi</p>
    </StyledFeatureImage>
  </StyledFeature>
)

export const RightFeature = () => (
  <StyledFeature>
    <StyledFeatureImage size="1" color={props => props.theme.black}>
      <p>hi</p>
    </StyledFeatureImage>
    <StyledFeatureBody size="2">
      <h2>Internet entertainment. Global original content. Product personalization.</h2>
      <p>Our first original series debuted in 2013. Over the following decades, Internet TV will replace linear, and we hope to keep loading by offering an amazing entertainment experience.</p>
    </StyledFeatureBody>
  </StyledFeature>
)
