import { useState, useEffect } from 'react'
import Link from 'next/link'
import NavItem from './NavItem'
import styled from 'styled-components'


const StyledNavLogo = styled.div`
  margin: 0 1em;
  text-transform: uppercase;
  line-height: 8rem;
  padding: 0 .94rem;
  height: 7rem;
  a {
    font-family: 'Raleway', sans-serif;
    font-size: 3.2rem;
  }
`

const NavLogo = () => (
  <StyledNavLogo>
    <Link href="/">
      <a>HAWKINS</a>
    </Link>
  </StyledNavLogo>
)

const StyledNavBar = styled.nav`
  display: flex;
  justify-content: space-between;
  background-color: ${props => props.theme.white};
  position: fixed;
  top: 0px;
  left: 0px;
  z-index: 1;
  margin: 0;
  width: 100%;
  /* height: 7rem; */
  /* padding: 0 5%; */
  box-shadow: 0px 4px 6px -1px rgba(0,0,0,0.1);
  transition: 250ms;

  ul {
    text-decoration: none;
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    font-size: 1.3rem;
  }

`

const StyledNavContainer = styled.div`
  .default-nav {
    background: transparent;
    box-shadow: inset 0 4rem 40px rgba(0,0,0,0.22);
    a {
      color: ${props => props.theme.white};
      :hover {
        color: ${props => props.theme.red};
      }
    }
  }
`

const NavBar = () => {
  const [isTop, setIsTop] = useState(true)

  useEffect(() => {


  }, [isTop])

  window.addEventListener('scroll', () => {
    if (window.scrollY > 2) {
      setIsTop(false)
    } else {
      setIsTop(true)
    }
  })

  const handleAdminHover = () => {
    console.log('hoveringggg')

  }

  return (
    <StyledNavContainer>
      <StyledNavBar className={isTop ? 'default-nav' : ''} id="nav">
        <NavLogo />
        <ul>
          <NavItem route="/library" pageName="Library" />
          <NavItem route="/events" pageName="Events" />
          <NavItem route="/return-books" pageName="Return Books" />
          <NavItem route="/join" pageName="Sign Up" />
          <NavItem route="/admin" pageName="Admin" onHover={handleAdminHover}/>
        </ul>
      </StyledNavBar>
    </StyledNavContainer>
  )
}


export default NavBar
