import { useState } from 'react'


const NoteApp = () => {
  const [notes, setNotes] = useState([])
  const [title, setTitle] = useState('')

  const addNote = (e) => {
    e.preventDefault()
    setNotes([
      ...notes,
      {title}
    ])
    setTitle('')
  }

  return (
    <div>
      {title}
      <h1>Notes</h1>
      <p>Add note</p>
      <form onSubmit={addNote}>
        <input 
          value={title} 
          onChange={(e) => {setTitle(e.target.value)}} 
        />

      </form>

    </div>
  )
}

export default NoteApp
