import styled from 'styled-components'

const StyledForm = styled.form`
`

const Divider = styled.hr`
  border: none;
  height: 3px;
  width: 40%;
  text-align: left;
  background: ${props => props.theme.red};
  margin: 1em 0;
  margin-bottom: 2em;
`

const FormFields = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`

const FormFieldRow = styled.div`
  display: flex;
  justify-content: space-between;
`


const FormField = styled.div`
  /* max-width: 1000px; */
  width: 100%;

  input {
    font-size: 1.6rem;
    background-color: ${props => props.theme.white};
    border: solid 1px ${props => props.theme.lightgray};
    line-height: 4rem;
    padding: 0 1rem;
    width: 100%;
    max-width: 500px;
    /* margin-right: 1em; */
    /* margin: 0 1em 1em 0; */
    box-sizing: border-box;
    &:focus {
      outline: none;
    }
  }
`
const Form = () => (

  <StyledForm>
    <p>We would love for you to join our library! As a patron, you get exclusive 
      crazy once in a life time never before seen vip limited time offer curated 
      privileged access to our vast 10 book collection. Did I look up synonnyms 
      for 'exclusive'? Yes but I only got like one or two words from the thesaurus. 
      Could I have used Lorem Ipsum instead? Yes. Is writing all of this a waste 
      of time? Nobody answer that.</p>
    <Divider />

    <FormFields>
      <FormFieldRow>

        <FormField>
          <label>First Name *</label><br />
          <input 
            type="text" 
            placeholder="Michael" 
            name="first-name" 
            id="first-name"
          />
        </FormField>

        <FormField>
          <label>Last Name</label><br />
          <input 
            type="text" 
            placeholder="Scott" 
            name="last-name" 
            id="last-name"
          />
        </FormField>

      </FormFieldRow>
      <FormFieldRow>

        <FormField>
          <label>Email</label><br />
          <input 
            type="email" 
            placeholder="michaelscott@dundermifflin.com" 
            name="email" 
            id="email"
          />
        </FormField>
        <FormField>
          <label>Phone</label><br />
          <input 
            type="phone" 
            placeholder="123-456-7890" 
            name="phone" 
            id="phone"
          />
        </FormField>

      </FormFieldRow>
    </FormFields>
  </StyledForm>
)

export default Form
