import { useState } from 'react'
import Link from 'next/link'
import NavItem from './NavItem'
import styled from 'styled-components'

import React, { Component } from 'react'


const StyledNavLogo = styled.div`
  margin: 0 1em;
  text-transform: uppercase;
  line-height: 8rem;
  padding: 0 .94rem;
  height: 7rem;
  a {
    color: ${props => props.theme.black};
    font-family: 'Raleway', sans-serif;
    font-size: 3.2rem;
  }
`

const NavLogo = () => (
  <StyledNavLogo>
    {/* <img src="/hawkins.png"></img> */}
    <Link href="/">
      <a>HAWKINS</a>
    </Link>
  </StyledNavLogo>
)

const StyledNavBar = styled.nav`
  display: flex;
  justify-content: space-between;
  /* background-color: ${props => props.theme.white}; */
  position: fixed;
  top: 0px;
  left: 0px;
  z-index: 1;
  margin: 0;
  width: 100%;
  /* height: 7rem; */
  /* padding: 0 5%; */
  box-shadow: 0px 4px 6px -1px rgba(0,0,0,0.1);

  ul {
    text-decoration: none;
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    font-size: 1.3rem;
  }

`

const StyledNavContainer = styled.div`
  .scrolled {
    background: red;
  }
`

class NavBar extends Component {
  componentDidMount() {
    window.addEventListener('scroll', () => {
      const isTop = window.scrollY > 100
      const navBar = document.getElementById('nav')
      if (isTop) {
        navBar.classList.add('scrolled')
      } else {
        navBar.classList.remove('scrolled')
      }
    })
  }
  componentWillUnmount() {
    window.removeEventListener('scroll')
  }
  render() {
    return (
      <StyledNavContainer>
        <StyledNavBar id="nav">
          <NavLogo />
          <ul>
            <NavItem route="/library" pageName="Library" />
            <NavItem route="/events" pageName="Events" />
            <NavItem route="/return-books" pageName="Return Books" />
            <NavItem route="/join" pageName="Sign Up" />
            <NavItem route="/admin" pageName="Admin" />
          </ul>
        </StyledNavBar>
      </StyledNavContainer>
    )
  }
}






