import { useState, useEffect } from 'react'


const NoteApp = () => {
  const [notes, setNotes] = useState([])
  const [title, setTitle] = useState('')
  const [body, setBody] = useState('')

  const addNote = (e) => {
    e.preventDefault()
    setNotes([
      ...notes,
      {
        title,
        body
      }
    ])
    setTitle('')
    setBody('')
  }

  const handleDeleteNote = (title) => {
    setNotes(notes.filter((note) => note.title !== title))
  }

  useEffect(() => {
    const notesData = JSON.parse(localStorage.getItem('notes'))

    if (notesData) {
      setNotes(notesData)
    }
  }, [])

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes))
  }, [notes])

  return (
    <div>
      <h1>Notes</h1>

      {/* Render state to page */}
      {notes.map((note) => (
        <Note key={note.title} note={note} handleDeleteNote={handleDeleteNote} />
      ))}

      <p>Add note</p>
      <form onSubmit={addNote}>

        <label>Title</label>
        <input 
          value={title} 
          onChange={(e) => {setTitle(e.target.value)}} 
        />

        <label>Body</label>
        <input 
          value={body} 
          onChange={(e) => {setBody(e.target.value)}} 
        />

        <button>add note</button>

      </form>

    </div>
  )
}

const Note = ({ note, handleDeleteNote }) => {
  useEffect(() => {
    console.log('setting up effect running on notsdfe');

    return () => {
      console.log('cleaning up use EFFECT');

    }
  })


  return (
    <div>
      <h3>{note.title}</h3>
      <p>{note.body}</p>
      <button onClick={() => handleDeleteNote(note.title)}>x</button>
    </div>
  )
}

