import styled from 'styled-components'

const ContactForm = styled.form`
  background-color: #fff;
  /* padding: 48px;
  box-shadow: 1px 1px 40px 0 rgba(0,0,0,.15);
  border-radius: 3px; */
`

const Divider = styled.hr`
  border: none;
  height: 3px;
  width: 40%;
  text-align: left;
  background: #302419;
  margin: 1em 0;
  margin-bottom: 2em;
`

const FormFields = styled.div`
`

const FormFieldRow = styled.div`
  display: flex;
  justify-content: space-between;
`


const FormField = styled.div`
  max-width: 1000px;
  width: 48%;

  input {
    font-size: 1em;
    background-color: #F9F5F1;
    border: none;
    padding: .8em;
    width: 100%;
    max-width: 500px;
    margin-right: 1em;
    margin: 0 1em 1em 0;
    box-sizing: border-box;
    &:focus {
      outline: none;
    }
  }
`
const Form = () => (

  <ContactForm>
    <p>We would love for you to join our library! As a patron, you get exclusive 
      crazy once in a life time never before seen vip limited time offer curated 
      privileged access to our vast 10 book collection. Did I look up synonnyms 
      for 'exclusive'? Yes. Could I have used Lorem Ipsum instead? Yes. Is 
      writing all of this a waste of time? Nobody answer that.</p>
    <Divider />

    <FormFields>
      <FormFieldRow>

        <FormField>
          <label>First Name</label><br />
          <input 
            type="text" 
            placeholder="Michael" 
            name="first-name" 
            id="first-name"
          />
        </FormField>

        <FormField>
          <label>Last Name</label><br />
          <input 
            type="text" 
            placeholder="Scott" 
            name="last-name" 
            id="last-name"
          />
        </FormField>

      </FormFieldRow>
      <FormFieldRow>

        <FormField>
          <label>Email</label><br />
          <input 
            type="email" 
            placeholder="michaelscott@dundermifflin.com" 
            name="email" 
            id="email"
          />
        </FormField>
        <FormField>
          <label>Phone</label><br />
          <input 
            type="phone" 
            placeholder="123-456-7890" 
            name="phone" 
            id="phone"
          />
        </FormField>

      </FormFieldRow>
    </FormFields>
  </ContactForm>
)

export default Form
