import styled from 'styled-components'

const HoverStyles = styled.div`
.hvr-underline-reveal {
  display: inline-block;
  vertical-align: middle;
  -webkit-transform: perspective(1px) translateZ(0);
  transform: perspective(1px) translateZ(0);
  box-shadow: 0 0 1px rgba(0, 0, 0, 0);
  position: relative;
  overflow: hidden;
}
.hvr-underline-reveal:before {
  content: "";
  position: absolute;
  z-index: -1;
  left: 0;
  right: 0;
  bottom: 0;
  background: #2098D1;
  height: 4px;
  -webkit-transform: translateY(4px);
  transform: translateY(4px);
  -webkit-transition-property: transform;
  transition-property: transform;
  -webkit-transition-duration: 0.3s;
  transition-duration: 0.3s;
  -webkit-transition-timing-function: ease-out;
  transition-timing-function: ease-out;
}
.hvr-underline-reveal:hover:before, .hvr-underline-reveal:focus:before, .hvr-underline-reveal:active:before {
  -webkit-transform: translateY(0);
  transform: translateY(0);
}
`


const StyledBook = styled.div`
  display: flex;
  flex-direction: column;
  box-shadow: -3px 10px 15px -3px rgba(0,0,0,0.3); /*lg shadow*/
  width: 320px;
  margin-bottom: 3.6rem;

  /* transition: 250ms;
  border-top: 1rem transparent;
  :hover {
    border-bottom: 1rem solid ${props => props.theme.red};
    transform: scale(1.02);
    cursor: pointer;
  } */
`

const StyledBookImage = styled.div`
  height: 200px;
  overflow: hidden;
  img {
    /* object-fit: cover; */
    width: 100%;
  }
`

const StyledBookInfo = styled.div`
  padding: 2rem 1rem 2rem 2rem;
  h2, p {
    margin: 0;
  }
`

const Book = props => (
  <HoverStyles>
    <StyledBook>
      <StyledBookImage>
        <img src={props.bookImgUrl}></img>
      </StyledBookImage>
      <StyledBookInfo>
        <h2>{props.bookTitle}</h2>
        <p>{props.bookAuthor}</p>
      </StyledBookInfo>
    </StyledBook>
  </HoverStyles>
)

export default Book
