import styled from 'styled-components'



const StyledFeature = styled.div`
  display: flex;
  max-width: ${props => props.theme.maxWidth};
  box-shadow: 0px 10px 15px -3px rgba(0,0,0,0.1); /*tailwindcss large shadow*/
  margin: auto;

`

const StyledFeatureBody = styled.div`
  /* background-color: ${props => props.theme.black}; */
  background-color: ${props => props.bgColor};
  flex: ${props => props.size};
  color: ${props => props.color};
`

const StyledFeatureImage = styled.div`
  background-color: ${props => props.theme.red};;
  flex: ${props => props.size};
  overflow: hidden;
  padding-left: ${props => props.paddingLeft};
  padding-right: ${props => props.paddingRight};
  height: 338px;
  img {
    height: 100%;
    /* object-fit: cover; */
    position: center;
    box-shadow: 0px 10px 15px -3px rgba(0,0,0,0.3); /*tailwindcss large shadow*/
  }

`

export const LeftFeature = () => (
  <StyledFeature>
    <StyledFeatureBody size="2" bgColor={props => props.theme.white} color={props => props.theme.black}>
      <h2>Internet entertainment. Global original content. Product personalization.</h2>
      <p>Our first original series debuted in 2013. Over the following decades, Internet TV will replace linear, and we hope to keep loading by offering an amazing entertainment experience.</p>
      <p></p>
    </StyledFeatureBody>
    <StyledFeatureImage size="1" paddingLeft="1.6rem" paddingRight="0">
      <img src="/feature03.jpeg"></img>
    </StyledFeatureImage>
  </StyledFeature>
)

export const RightFeature = () => (
  <StyledFeature>
    <StyledFeatureImage size="1" paddingLeft="0" paddingRight="0">
      <img src="/feature01.jpeg"></img>
    </StyledFeatureImage>
    <StyledFeatureBody size="2" bgColor={props => props.theme.black} color={props => props.theme.white}>
      <h2>Internet entertainment. Global original content. Product personalization.</h2>
      <p>Our first original series debuted in 2013. Over the following decades, Internet TV will replace linear, and we hope to keep loading by offering an amazing entertainment experience.</p>
    </StyledFeatureBody>
  </StyledFeature>
)
