import { useState } from 'react'
import Link from 'next/link'
import NavItem from './nav/NavItem'
import styled from 'styled-components'

const StyledFooterLogo = styled.div`
  margin: 0 1em;
  text-transform: uppercase;
  /* line-height: 8rem; */
  padding: 0 .94rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  height: 100%;
  background: gray;
`

const FlexContainer = styled.div`
  /* display: flex;
  flex-direction: ${props => props.direction}; */
`

const FooterLogo = () => (
  <div>
    <StyledFooterLogo>
        <Link href="/">
          <a>Logo</a>
        </Link>


    </StyledFooterLogo>
  </div>
)

const StyledFooter = styled.footer`
  display: flex;
  justify-content: space-between;
  background-color: ${props => props.theme.white};
  /* position: fixed;
  top: 0px;
  left: 0px; */
  z-index: 1;
  margin: 0;
  width: 100%;
  /* height: 7rem; */
  /* background: red; */
  /* padding: 0 5%; */
  /* box-shadow: 0px 4px 6px -1px rgba(0,0,0,0.1); */

  ul {
    text-decoration: none;
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    font-size: 1.3rem;
  }
`

const StyledFooterContainer = styled.div`
  padding: 2.4rem 0;
  /* background: blue; */

`

const StyledFooterTopRow = styled.div`
  display: flex;
  justify-content: flex-end;
  li {
    margin: 0 1.6rem;
  }
  padding-bottom: 1.6rem;
`

const StyledFooterBottomRow = styled.div`
  display: flex;
  justify-content: flex-end;
  font-size: 1.2rem;
  /* padding-bottom: 3.6rem; */
  /* background: green; */
`

const StyledFooterRowContainer = styled.div`
  display: flex;
  flex-direction: column;
  h4, p {
    margin: 0 1.6rem;
  }
  p {
    color: ${props => props.theme.grey};
  }
  /* background: pink; */
`

const Footer = () => (
  <StyledFooterContainer>
    <StyledFooter>
      <FooterLogo />
      <StyledFooterRowContainer>
        <StyledFooterTopRow>
          <ul>
            <li>Follow us</li>
            <li>FB</li>
            <li>TW</li>
            <li>GIT</li>
            <li>IN</li>
          </ul>
        </StyledFooterTopRow>
        {/* <ul>
          <NavItem route="/library" pageName="Library" />
          <NavItem route="/events" pageName="Events" />
          <NavItem route="/return-books" pageName="Return Books" />
          <NavItem route="/join" pageName="Sign Up" />
          <NavItem route="/admin" pageName="Admin" />
        </ul> */}
        <StyledFooterBottomRow>
          <h4>Hawkins Public Library</h4>
          <p>Images from Unsplash</p>
        </StyledFooterBottomRow>
      </StyledFooterRowContainer>
    </StyledFooter>
  </StyledFooterContainer>

)

export default Footer
