import styled from 'styled-components'

const ContactForm = styled.form`
  background-color: #fff;
  padding: 48px;
  box-shadow: 1px 1px 40px 0 rgba(0,0,0,.15);
  border-radius: 3px;
`

const Divider = styled.hr`
  border: none;
  height: 3px;
  width: 40%;
  text-align: left;
  background: #302419;
  margin: 1em 0;
  margin-bottom: 2em;
`

const FormFields = styled.div`
`

const FormFieldRow = styled.div`
  display: flex;
  justify-content: space-between;
`


const FormField = styled.div`
  max-width: 1000px;
  width: 48%;

  input {
    font-size: 1em;
    background-color: #F9F5F1;
    border: none;
    padding: .8em;
    width: 100%;
    max-width: 500px;
    margin-right: 1em;
    margin: 0 1em 1em 0;
    box-sizing: border-box;
    &:focus {
      outline: none;
    }
  }
`
const Form = () => (

  <ContactForm>
    <h3 className="hero-heading">I'd love to hear from you!</h3>
    <h5 className="">Just fill out the form below, and I'll be in touch to set up a call.</h5>
    <p>Note: This site is loaded over HTTPS and sends a POST request to http://httpbin.org/post per assignment requirements (an insecure resource). If the browser blocks the request and you get an error, check the console for details. In order for POST request to work, you may have to load unsafe scripts.</p>
    <Divider />

    <FormFields>
      <FormFieldRow>

        <FormField>
          <label>Name</label><br />
          <input 
            type="text" 
            placeholder="Michael Scott" 
            name="name" 
            id="name"
          />
        </FormField>

        <FormField>
          <label>Email</label><br />
          <input 
            type="email" 
            placeholder="email@dundermifflin.com" 
            name="email" 
            id="email"
          />
        </FormField>

      </FormFieldRow>
      <FormFieldRow>

        <FormField>
          <label>Phone</label><br />
          <input 
            type="text" 
            placeholder="123-456-7890" 
            name="phone" 
            id="phone"
          />
        </FormField>
        <FormField>
          <label>Company</label><br />
          <input 
            type="text" 
            placeholder="Dunder Mifflin" 
            name="company" 
            id="company"
          />
        </FormField>

      </FormFieldRow>
    </FormFields>
  </ContactForm>
)

export default Form
