import styled from 'styled-components'
import Container, { BannerContainer } from '../components/styles/Container'


const StyledBanner = styled.div`
  background-color: ${props => props.theme.black};
  height: 280px;
`

const StyledBannerContent = styled.div`
  width: ${props => props.theme.maxWidthBanner};
  color: ${props => props.theme.white};
  height: 300px;
  padding-top: 8rem;
  img {
    object-fit: cover;
    padding-top: 1.6rem;
    width: 100%;
    height: 340px;
  }
`


const PageBanner = () => (
  <StyledBanner>
    <BannerContainer>
      <StyledBannerContent>
        <img src="/banners/library-banner.jpeg"></img>

      </StyledBannerContent>
    </BannerContainer>
  </StyledBanner>
)

export default PageBanner
